import { useState, useEffect } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { useLanguage } from "@/lib/languageContext";
import { RadioGroup, RadioGroupItem } from "@/components/ui/radio-group";
import { Label } from "@/components/ui/label";

// Import your local image
import localImage from "@/assets/images/your-image.jpg";

const THEMES = [
  {
    id: "bubble-tea",
    name: {
      en: "Bubble Tea 1",
      ar: "شاي الفقاعة 1",
      he: "בועה תה 1"
    },
    bgImage: `url(${localImage})`
  },
  {
    id: "bubble-tea-2",
    name: {
      en: "Bubble Tea 2",
      ar: "شاي الفقاعة 2",
      he: "בועה תה 2"
    },
    bgImage: `url(${localImage})`
  },
  {
    id: "bubble-tea-3",
    name: {
      en: "Bubble Tea 3",
      ar: "شاي الفقاعة 3",
      he: "בועה תה 3"
    },
    bgImage: `url(${localImage})`
  },
  {
    id: "bubble-tea-4",
    name: {
      en: "Bubble Tea 4",
      ar: "شاي الفقاعة 4",
      he: "בועה תה 4"
    },
    bgImage: `url(${localImage})`
  }
];

const ThemeSelector = () => {
  const { t, language } = useLanguage();
  const [selectedTheme, setSelectedTheme] = useState("bubble-tea");

  useEffect(() => {
    // Load theme from localStorage
    const storedTheme = localStorage.getItem("app_theme");
    if (storedTheme) {
      setSelectedTheme(storedTheme);
    }
  }, []);

  const handleThemeChange = (value: string) => {
    setSelectedTheme(value);
    localStorage.setItem("app_theme", value);
  };

  return (
    <Card>
      <CardHeader>
        <CardTitle>{t("settings")}</CardTitle>
      </CardHeader>
      <CardContent>
        <div className="space-y-4">
          <h3 className="text-lg font-medium">Theme</h3>

          <RadioGroup value={selectedTheme} onValueChange={handleThemeChange}>
            <div className="grid grid-cols-2 gap-4">
              {THEMES.map((theme) => (
                <div key={theme.id} className="flex items-center space-x-2">
                  <RadioGroupItem value={theme.id} id={theme.id} />
                  <Label 
                    htmlFor={theme.id}
                    className="flex items-center gap-2"
                  >
                    <div 
                      className="w-8 h-8 rounded-full border" 
                      style={{ backgroundImage: theme.bgImage, backgroundSize: 'cover' }}
                    />
                    <span>{theme.name[language]}</span>
                  </Label>
                </div>
              ))}
            </div>
          </RadioGroup>
        </div>
      </CardContent>
    </Card>
  );
};

export default ThemeSelector;
```

### Steps to finalize:
1. Replace `your-image.jpg` with the actual name of your image file.
2. Ensure the image is placed in the correct folder (e.g., `src/assets/images/`).
3. Update the import path (`@/assets/images/your-image.jpg`) if your folder structure is different.

Let me know if you need further assistance!

