import { useState, useEffect } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { useLanguage } from "@/lib/languageContext";
import { RadioGroup, RadioGroupItem } from "@/components/ui/radio-group";
import { Label } from "@/components/ui/label";

// Import your local image
import localImage from "@/assets/images/theme-background.jpg";

const THEMES = [
  { id: "category_taste", bgImage: `url(${localImage})` },
  { id: "category_service", bgImage: `url(${localImage})` },
  { id: "category_price", bgImage: `url(${localImage})` },
  { id: "category_look", bgImage: `url(${localImage})` },
  { id: "category_other", bgImage: `url(${localImage})` },
];

const ThemeSelector = () => {
  const { t, language } = useLanguage(); // Get the current language
  const [selectedTheme, setSelectedTheme] = useState("category_taste");

  useEffect(() => {
    // Load theme from localStorage
    const storedTheme = localStorage.getItem("app_theme");
    if (storedTheme) {
      setSelectedTheme(storedTheme);
    }
  }, []);

  const handleThemeChange = (value: string) => {
    setSelectedTheme(value);
    localStorage.setItem("app_theme", value);
  };

  return (
    <Card>
      <CardHeader>
        <CardTitle>{t("settings")}</CardTitle>
      </CardHeader>
      <CardContent>
        <div className="space-y-4">
          <h3 className="text-lg font-medium">{t("theme")}</h3>

          <RadioGroup value={selectedTheme} onValueChange={handleThemeChange}>
            <div className="grid grid-cols-2 gap-4">
              {THEMES.map((theme) => (
                <div key={theme.id} className="flex items-center space-x-2">
                  <RadioGroupItem value={theme.id} id={theme.id} />
                  <Label
                    htmlFor={theme.id}
                    className="flex items-center gap-2"
                  >
                    <div
                      className="w-8 h-8 rounded-full border"
                      style={{
                        backgroundImage: theme.bgImage,
                        backgroundSize: "cover",
                      }}
                    />
                    <span>{t(theme.id)}</span> {/* Use the current language */}
                  </Label>
                </div>
              ))}
            </div>
          </RadioGroup>
        </div>
      </CardContent>
    </Card>
  );
};

export default ThemeSelector;
```

