import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import DashboardHeader from "@/components/DashboardHeader";
import FeedbackChart from "@/components/FeedbackChart";
import FeedbackList from "@/components/FeedbackList";
import { useDashboardAuth } from "@/hooks/use-dashboard-auth";
import { useLanguage } from "@/lib/languageContext";
import { Feedback, FeedbackFilters } from "@/lib/types";
import { 
  getAllFeedback, 
  getStorageLocation, 
  setStorageLocation 
} from "@/lib/feedbackService";
import PasswordChangeForm from "@/components/PasswordChangeForm";
import { Input } from "@/components/ui/input";
import { toast } from "sonner";

const Dashboard = () => {
  const navigate = useNavigate();
  const { t } = useLanguage();
  const { isAuthenticated, logout } = useDashboardAuth();

  const [feedback, setFeedback] = useState<Feedback[]>([]);
  const [isLoading, setIsLoading] = useState(true);
  const [filters, setFilters] = useState<FeedbackFilters>({});
  const [settingsTab, setSettingsTab] = useState("password");
  const [storageLocation, setStorageLocationState] = useState("");
  const [newStorageLocation, setNewStorageLocation] = useState("");

  // Redirect if not authenticated
  useEffect(() => {
    if (!isAuthenticated) {
      navigate("/dashboard-login");
    }
  }, [isAuthenticated, navigate]);

  // Load feedback and storage location
  useEffect(() => {
    const loadData = async () => {
      try {
        setIsLoading(true);
        const data = await getAllFeedback(filters);
        setFeedback(data);

        // Get current storage location
        setStorageLocationState(getStorageLocation());
        setNewStorageLocation(getStorageLocation());
      } catch (error) {
        console.error("Error loading data:", error);
      } finally {
        setIsLoading(false);
      }
    };

    loadData();
  }, [filters]);

  const handleLogout = () => {
    logout();
    navigate("/");
  };

  const handleUpdateStorageLocation = () => {
    try {
      setStorageLocation(newStorageLocation);
      setStorageLocationState(newStorageLocation);
      toast.success(t("storage_location_updated") || "Storage location updated");
    } catch (error) {
      console.error("Error updating storage location:", error);
      toast.error(t("error_updating_storage") || "Error updating storage location");
    }
  };

  return (
    <div className="min-h-screen bg-gray-900 text-white"> {/* Improved contrast */}
      <DashboardHeader onLogout={handleLogout} />

      <main className="container mx-auto p-4 pt-20">
        <Tabs defaultValue="overview" className="space-y-4">
          <TabsList>
            <TabsTrigger value="overview">{t("feedback_analytics")}</TabsTrigger>
            <TabsTrigger value="list">{t("recent_feedback")}</TabsTrigger>
            <TabsTrigger value="settings">{t("settings")}</TabsTrigger>
          </TabsList>

          <TabsContent value="overview" className="space-y-4">
            <FeedbackChart feedback={feedback} isLoading={isLoading} />
          </TabsContent>

          <TabsContent value="list" className="space-y-4">
            <FeedbackList feedback={feedback} isLoading={isLoading} />
          </TabsContent>

          <TabsContent value="settings" className="space-y-4">
            <Card>
              <CardHeader>
                <CardTitle>{t("settings")}</CardTitle>
              </CardHeader>
              <CardContent>
                <Tabs value={settingsTab} onValueChange={setSettingsTab}>
                  <TabsList className="mb-4">
                    <TabsTrigger value="password">{t("change_password")}</TabsTrigger>
                    <TabsTrigger value="storage">{t("storage_location")}</TabsTrigger>
                  </TabsList>

                  <TabsContent value="password">
                    <PasswordChangeForm />
                  </TabsContent>

                  <TabsContent value="storage">
                    <div className="space-y-4 max-w-md">
                      <h2 className="text-xl font-semibold">
                        {t("storage_location")}
                      </h2>

                      <div className="space-y-2">
                        <p className="text-sm text-gray-300">
                          {t("current_location") || "Current location"}: {storageLocation}
                        </p>

                        <div className="flex space-x-2">
                          <Input 
                            value={newStorageLocation}
                            onChange={(e) => setNewStorageLocation(e.target.value)}
                            placeholder="/storage/emulated/0/Documents/Feedback"
                          />
                          <Button onClick={handleUpdateStorageLocation}>
                            {t("set_location")}
                          </Button>
                        </div>
                      </div>
                    </div>
                  </TabsContent>
                </Tabs>
              </CardContent>
            </Card>
          </TabsContent>
        </Tabs>
      </main>
    </div>
  );
};

export default Dashboard;
```

