import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import DashboardHeader from "@/components/DashboardHeader";
import FeedbackChart from "@/components/FeedbackChart";
import FeedbackList from "@/components/FeedbackList";
import { useLanguage } from "@/lib/languageContext";
import { getAllFeedback } from "@/lib/feedbackService";

const Dashboard = () => {
  const { t } = useLanguage();
  const navigate = useNavigate();
  const [feedback, setFeedback] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [selectedMonth, setSelectedMonth] = useState("");
  const [satisfactionPercentage, setSatisfactionPercentage] = useState(0);

  useEffect(() => {
    const loadFeedback = async () => {
      try {
        setIsLoading(true);
        const data = await getAllFeedback({ month: selectedMonth });
        setFeedback(data);

        // Calculate satisfaction percentage
        const satisfied = data.filter((item) => item.satisfaction === "good").length;
        const total = data.length;
        setSatisfactionPercentage(total > 0 ? Math.round((satisfied / total) * 100) : 0);
      } catch (error) {
        console.error("Error loading feedback:", error);
      } finally {
        setIsLoading(false);
      }
    };

    loadFeedback();
  }, [selectedMonth]);

  return (
    <div className="min-h-screen bg-gray-900 text-white">
      <DashboardHeader onLogout={() => navigate("/logout")} />

      <main className="container mx-auto p-4 pt-20">
        <Tabs defaultValue="overview" className="space-y-4">
          <TabsList>
            <TabsTrigger value="overview">{t("feedback_analytics")}</TabsTrigger>
            <TabsTrigger value="list">{t("recent_feedback")}</TabsTrigger>
            <TabsTrigger value="settings">{t("settings")}</TabsTrigger>
          </TabsList>

          <TabsContent value="overview" className="space-y-4">
            <div className="flex items-center space-x-4">
              <label htmlFor="month-filter" className="text-sm">
                {t("filter_by_month")}
              </label>
              <select
                id="month-filter"
                value={selectedMonth}
                onChange={(e) => setSelectedMonth(e.target.value)}
                className="bg-gray-800 text-white rounded px-2 py-1"
              >
                <option value="">{t("all")}</option>
                <option value="01">{t("January")}</option>
                <option value="02">{t("February")}</option>
                <option value="03">{t("March")}</option>
                <option value="04">{t("April")}</option>
                <option value="05">{t("May")}</option>
                <option value="06">{t("June")}</option>
                <option value="07">{t("July")}</option>
                <option value="08">{t("August")}</option>
                <option value="09">{t("September")}</option>
                <option value="10">{t("October")}</option>
                <option value="11">{t("November")}</option>
                <option value="12">{t("December")}</option>
              </select>
            </div>
            <p className="text-lg mt-4">
              {t("satisfaction_percentage")}: {satisfactionPercentage}%
            </p>
            <FeedbackChart feedback={feedback} isLoading={isLoading} />
          </TabsContent>

          <TabsContent value="list" className="space-y-4">
            <FeedbackList
              feedback={feedback}
              isLoading={isLoading}
              className="bg-gray-800 text-white p-4 rounded"
            />
          </TabsContent>
        </Tabs>
      </main>
    </div>
  );
};

export default Dashboard;
```

