import { Button } from "@/components/ui/button";
import { useLanguage } from "@/lib/languageContext";

const Settings = () => {
  const { t } = useLanguage();
  const [storageLocation, setStorageLocation] = useState(
    localStorage.getItem("storageLocation") || ""
  );
  const [newPassword, setNewPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");

  const handleStorageChange = () => {
    localStorage.setItem("storageLocation", storageLocation);
    alert(t("storage_location_updated"));
  };

  const handlePasswordChange = () => {
    if (newPassword !== confirmPassword) {
      alert(t("passwords_not_match"));
      return;
    }
    // Save the new password (this is just a placeholder, implement securely)
    alert(t("password_changed"));
  };

  return (
    <div className="p-4">
      <Card>
        <CardHeader>
          <CardTitle>{t("storage_location")}</CardTitle>
        </CardHeader>
        <CardContent>
          <Input
            value={storageLocation}
            onChange={(e) => setStorageLocation(e.target.value)}
            placeholder={t("set_location")}
          />
          <Button onClick={handleStorageChange} className="mt-2">
            {t("save")}
          </Button>
        </CardContent>
      </Card>

      <Card className="mt-4">
        <CardHeader>
          <CardTitle>{t("change_password")}</CardTitle>
        </CardHeader>
        <CardContent>
          <Input
            type="password"
            value={newPassword}
            onChange={(e) => setNewPassword(e.target.value)}
            placeholder={t("new_password")}
          />
          <Input
            type="password"
            value={confirmPassword}
            onChange={(e) => setConfirmPassword(e.target.value)}
            placeholder={t("confirm_password")}
            className="mt-2"
          />
          <Button onClick={handlePasswordChange} className="mt-2">
            {t("save")}
          </Button>
        </CardContent>
      </Card>
    </div>
  );
};

export default Settings;
