          <TabsList>
            <TabsTrigger value="overview">{t("feedback_analytics")}</TabsTrigger>
            <TabsTrigger value="list">{t("recent_feedback")}</TabsTrigger>
            <TabsTrigger value="settings">{t("settings")}</TabsTrigger>
          </TabsList>

          <TabsContent value="overview" className="space-y-4">
            <div className="flex items-center space-x-4">
              <label htmlFor="month-filter" className="text-sm">
                {t("filter_by_month")}</label>
              <select
                id="month-filter"
                value={selectedMonth}
                onChange={handleMonthChange}
                className="bg-gray-800 text-white rounded px-2 py-1"
              >
                <option value="">{t("all")}</option>
                {availableMonths.map((month) => (
                  <option key={month} value={month}>{t(month)}</option> // Assuming you have translations for month numbers
                ))}
              </select>

              <label htmlFor="year-filter" className="text-sm">
                {t("filter_by_year")}</label>
              <select
                id="year-filter"
                value={selectedYear}
                onChange={handleYearChange}
                className="bg-gray-800 text-white rounded px-2 py-1"
              >
                <option value="">{t("all")}</option>
                {availableYears.map((year) => (
                  <option key={year} value={year}>{year}</option>
                ))}
              </select>
            </div>
            <p className="text-lg mt-4">
              {t("satisfaction_percentage")}: {satisfactionPercentage}%
            </p>
            <FeedbackChart feedback={feedback} isLoading={isLoading} />
          </TabsContent>

          <TabsContent value="list" className="space-y-4">
            <FeedbackList
              feedback={feedback}
              isLoading={isLoading}
              className="bg-gray-800 text-white p-4 rounded"
            />
          </TabsContent>

          <TabsContent value="settings" className="space-y-4">
            <Settings />
          </TabsContent>
        </Tabs>
      </main>
    </div>
  );
