import { useState } from "react";
import { format } from "date-fns";
import { ThumbsDown, ThumbsUp, Trash2, Search, X } from "lucide-react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Separator } from "@/components/ui/separator";
import { Feedback } from "@/lib/types";
import LoadingSpinner from "./LoadingSpinner";
import { useLanguage } from "@/lib/languageContext";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { 
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
} from "@/components/ui/alert-dialog";
import { toast } from "sonner";
import { feedbackService } from "@/lib/feedbackService";

interface FeedbackListProps {
  feedback: Feedback[];
  isLoading?: boolean;
  onDeleteFeedback?: () => void;
}

const FeedbackList = ({ feedback, isLoading = false, onDeleteFeedback }: FeedbackListProps) => {
  const { t } = useLanguage();
  const [searchQuery, setSearchQuery] = useState("");
  const [feedbackToDelete, setFeedbackToDelete] = useState<string | null>(null);

  // Filter feedback by search query
  const filteredFeedback = searchQuery.trim() === "" 
    ? feedback 
    : feedback.filter(item => {
        const query = searchQuery.toLowerCase();
        const searchableText = [
          item.message || "",
          item.contactName || "",
          item.contactPhone || "",
          t(`category_${item.category}`),
          t(`type_${item.type}`)
        ].join(" ").toLowerCase();

        return searchableText.includes(query);
      });

  const handleDeleteFeedback = async () => {
    if (!feedbackToDelete) return;

    try {
      await feedbackService.deleteFeedbackById(feedbackToDelete);
      toast.success(t("feedback_deleted"));
      setFeedbackToDelete(null);

      // Call the callback to refresh feedback data
      if (onDeleteFeedback) {
        onDeleteFeedback();
      }
    } catch (error) {
      console.error("Error deleting feedback:", error);
      toast.error(t("error_deleting_feedback"));
    }
  };

  const clearSearch = () => {
    setSearchQuery("");
  };

  if (isLoading) {
    return (
      <Card className="shadow-sm">
        <CardHeader className="pb-2">
          <CardTitle className="text-xl text-black bg-white/90 px-2 py-1 rounded inline-block">{t("recent_feedback")}</CardTitle>
        </CardHeader>
        <CardContent className="text-black"> {/* Added text-black class */}
          <div className="py-10 flex justify-center">
            <LoadingSpinner />
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card className="shadow-sm">
      <CardHeader className="pb-2">
        <div className="flex items-center justify-between flex-wrap gap-2">
          <CardTitle className="text-xl text-black bg-white/90 px-2 py-1 rounded">
            {searchQuery.trim() !== "" ? t("search_results") : t("recent_feedback")}
          </CardTitle>
          <div className="relative flex-1 md:max-w-xs">
            <Input
              placeholder={t("search_placeholder")}
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="pr-8 bg-white text-black"
              dir="auto"
            />
            {searchQuery && (
              <Button
                variant="ghost"
                size="icon"
                className="absolute right-0 top-0 h-full"
                onClick={clearSearch}
                title={t("clear_search")}
              >
                <X className="h-4 w-4" />
              </Button>
            )}
            {!searchQuery && (
              <Search className="absolute right-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-500" />
            )}
          </div>
        </div>
      </CardHeader>
      <CardContent>
        {filteredFeedback.length === 0 ? (
          <div className="py-10 text-center">
            <p className="text-black bg-white/90 px-2 py-1 rounded inline-block">
              {searchQuery.trim() !== "" ? t("no_results") : t("no_feedback")}
            </p>
            {searchQuery.trim() !== "" && (
              <Button variant="outline" onClick={clearSearch} className="mt-2">
                {t("clear_search")}
              </Button>
            )}
          </div>
        ) : (
          <div className="space-y-4">
            {filteredFeedback.map((item) => (
              <div key={item.id} className="rounded-lg border p-4 bg-white">
                <div className="flex items-center justify-between">
                  <div className="flex items-center gap-2">
                    <div className={`p-1.5 rounded-full ${item.type === "good" ? "bg-good/10" : "bg-bad/10"}`}>
                      {item.type === "good" ? (
                        <ThumbsUp className="h-4 w-4 text-good" />
                      ) : (
                        <ThumbsDown className="h-4 w-4 text-bad" />
                      )}
                    </div>
                    <span className="font-medium text-black">
                      {t(`feedback_${item.type}`)}
                    </span>
                    <span className="text-black">•</span>
                    <span className="text-sm text-black">
                      {t(`category_${item.category}`)}
                    </span>
                  </div>
                  <div className="flex items-center gap-2">
                    <span className="text-sm text-black">
                      {format(new Date(item.createdAt), "MMM d, yyyy 'at' h:mm a")}
                    </span>
                    <Button 
                      variant="ghost" 
                      size="icon"
                      className="h-8 w-8 text-red-500 hover:text-red-700 hover:bg-red-100"
                      onClick={() => setFeedbackToDelete(item.id)}
                    >
                      <Trash2 className="h-4 w-4" />
                    </Button>
                  </div>
                </div>

                {item.message && (
                  <>
                    <Separator className="my-3" />
                    <p className="text-sm dir-auto text-black font-medium">{item.message}</p>
                  </>
                )}

                {(item.contactName || item.contactPhone) && (
                  <>
                    <Separator className="my-3" />
                    <div className="text-sm text-black">
                      <p className="font-bold text-black">{t("contact_details")}:</p>
                      {item.contactName && <p className="text-black">{t("name")}: {item.contactName}</p>}
                      {item.contactPhone && <p className="text-black">{t("phone")}: {item.contactPhone}</p>}
                    </div>
                  </>
                )}
              </div>
            ))}
          </div>
        )}
      </CardContent>

      {/* Confirmation Dialog */}
      <AlertDialog open={!!feedbackToDelete} onOpenChange={(isOpen) => !isOpen && setFeedbackToDelete(null)}>
        <AlertDialogContent>
          <AlertDialogHeader>
            <AlertDialogTitle className="text-black">{t("confirm_delete")}</AlertDialogTitle>
            <AlertDialogDescription className="text-black">
              {t("delete_confirmation_message")}
            </AlertDialogDescription>
          </AlertDialogHeader>
          <AlertDialogFooter className="reverse-flex">
            <AlertDialogCancel>{t("cancel")}</AlertDialogCancel>
            <AlertDialogAction onClick={handleDeleteFeedback} className="bg-red-500 hover:bg-red-600">
              {t("confirm")}
            </AlertDialogAction>
          </AlertDialogFooter>
        </AlertDialogContent>
      </AlertDialog>
    </Card>
  );
};

export default FeedbackList;
```

